package com.valens.xml.utils;

import java.awt.image.BufferedImage;
import java.io.ByteArrayOutputStream;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;

import javax.imageio.ImageIO;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

/**
 * xml传图片 1.把图片读到byte[] 流
 * 
 * 2.把byte[]流转为BASE64编码（即是一堆字符串），把字符串放到XML里，图片就可以随着xml传输了。
 * 
 * 3.把xml里的BASE64编码解码为byte[]流，把再输出为图片。
 * 
 * @author Administrator
 * 
 */
public class XmlToImg {
	// 实例化 处理BASE64Encoder对象
	static BASE64Encoder encoder = new sun.misc.BASE64Encoder();

	/**
	 * 得到指定图片的base64编码
	 */
	public static String getImageBinary(String imgPath) {
		File f = new File(imgPath);
		BufferedImage bi;
		try {
			bi = ImageIO.read(f);
			ByteArrayOutputStream baos = new ByteArrayOutputStream();
			ImageIO.write(bi, "jpg", baos);
			byte[] bytes = baos.toByteArray();

			return encoder.encodeBuffer(bytes).trim();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return null;
	}
	/**
	 * 图片的base64编码转换成图片
	 * @param imgStr
	 * @param imgSavePath
	 * @throws IOException
	 */
	public static void strToImg(String imgStr, String imgSavePath)
			throws IOException {

		// str 为一图片的BASE64编码

		// String imgStr =
		// "";

		BASE64Decoder decoder = new BASE64Decoder();

		byte[] bytes = decoder.decodeBuffer(imgStr);

		File file = new File(imgSavePath);

		FileOutputStream fos = new FileOutputStream(file);

		fos.write(bytes);

		fos.flush();

		fos.close();

	}
	public static void main(String[] s){
		 String imgStr ="";
		 imgStr="";
		 imgStr="";
		 imgStr="";
		 try {
			//String imgPath="E:/opt/test/QQ0.png";
			//imgStr=XmlToImg.getImageBinary(imgPath);
			XmlToImg.strToImg(imgStr, "E:/opt/test/test3.jpg");
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

}
